        public static string HangulToKanji(string hangul)
        {
            StringBuilder sb = new StringBuilder();
            foreach (char c in hangul)
            {
                int index = Array.IndexOf(HangulChars, c);
                if (index != -1)
                {
                    sb.Append(HangulMappedKanjiChars[index]);
                }
                else
                {
                    sb.Append(c);
                }
            }
            return sb.ToString();
